.shipping-radio {
    display: block;
    position: relative;
    padding-left: 28px;
    margin-bottom: 14px;
    cursor: pointer;
    font-size: 14px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    font-weight: 400;
    color: #272B41;
}
