.scroll-center {
	display: block;
	margin: 0 auto;
	width: 24px;
	height: 100px;
	margin-top: 125px;
}
.scroll-arrow-one {
    display: block;
    -ms-transform: rotate(45deg);
    -webkit-transform: rotate(45deg);
    transform: rotate(45deg);
    border-right: 1px solid #7E8998;
    border-bottom: 1px solid #7E8998;
    margin: -8px 0 8px 5px;
    width: 14px;
    height: 14px;
    -webkit-animation: mouse-scroll .4s infinite;
    -moz-animation: mouse-scroll .4s infinite;
    -webkit-animation-direction: alternate;
}
.scroll-arrow-two {
	display: block;
    -ms-transform: rotate(45deg);
    -webkit-transform: rotate(45deg);
    transform: rotate(45deg);
    border-right: 1px solid #7E8998;
    border-bottom: 1px solid #7E8998;
    margin: -14px 0 8px 5px;
    width: 14px;
    height: 14px;
    -webkit-animation: mouse-scroll .8s infinite;
    -moz-animation: mouse-scroll .8s infinite;
    -webkit-animation-direction: alternate;
}
.scroll-mouse {
	height: 42px;
	width: 24px;
	border-radius: 14px;
	transform: none;
	border: 2px solid #7E8998;
	margin-bottom: 6px;
}
.scroll-wheel {
	height: 5px;
	width: 2px;
	display: block;
	margin: 5px auto;
	background: #7E8998;
	position: relative;
	height: 4px;
	width: 4px;
	border: 2px solid #7E8998;
	border-radius: 8px;
	-webkit-animation: mouse-wheel 2s linear infinite;
	-moz-animation: mouse-wheel 2s linear infinite;
}
@-webkit-keyframes mouse-wheel {
  0% {
    opacity: 1;
    -webkit-transform: translateY(0);
    -ms-transform: translateY(0);
    transform: translateY(0);
  }
  100% {
    opacity: 0;
    -webkit-transform: translateY(20px);
    -ms-transform: translateY(20px);
    transform: translateY(20px);
  }
}
@-moz-keyframes mouse-wheel {
  0% {
    top: 1px;
  }
  25% {
    top: 2px;
  }
  50% {
    top: 6px;
  }
  75% {
    top: 2px;
  }
  100% {
    top: 1px;
  }
}
@-webkit-keyframes mouse-scroll {
  0% {
    opacity: 0;
  }
  25% {
   opacity: .25;
  }
  50% {
    opacity: .5;
  }
  75% {
    opacity: .75;
  }
  100% {
    opacity: 1;
  }
}
@-moz-keyframes mouse-scroll {
  0% {
    opacity: 0;
  }
  50% {
    opacity: .5;
  }
  100% {
    opacity: 1;
  }
}
@-o-keyframes mouse-scroll {
  0% {
    opacity: 0;
  }
  25% {
   opacity: .25;
  }
  50% {
    opacity: .5;
  }
  75% {
    opacity: .75;
  }
  100% {
    opacity: 1;
  }
}
@keyframes mouse-scroll {
  0% {
    opacity: 0;
  }
  25% {
   opacity: .25;
  }
  50% {
    opacity: .5;
  }
  75% {
    opacity: .75;
  }
  100% {
