.tog-slider {
	position: absolute;
	cursor: pointer;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background-color: #F5F6FA;
	-webkit-transition: .4s;
	transition: .4s;
}
.tog-slider:before {
	position: absolute;
	content: "";
	height: 16px;
	width: 16px;
	bottom: 4px;
	background-color: #008F64;
	-webkit-transition: .4s;
	transition: .4s;
	top: 4px;
	left: 4px;
}
input:checked+.tog-slider {
	background-color: #008F64;
}
input:checked+.tog-slider:before {
	-webkit-transform: translateX(32px);
	-ms-transform: translateX(32px);
	transform: translateX(32px);
	background: #fff;
	left: -1px;
}
.tog-slider.round {
	border-radius: 30px;
	width: 56px;
	height: 28px;
	border: 2px solid #F3F3F3;
}
.tog-slider.round:before {
	border-radius: 50%;
}
