.button {
	display: inline-block;
	position: relative;
	background-color: #fff;
	font-size: 16px;
	cursor: pointer;
	border:none;
	min-width: 160px;
	transition: all 0.3s;
  }
  .button::after {
	content: '';
	width: 155px;
	height: 6px;
	background-color: #E5F1FB;
	border-radius: 5px;
	position: absolute;
	bottom: -20px;
    left: 0px;
  }
  .button:hover {
	color:#0071DC ;
  }
  
  /* .button:active,
  .button.is-checked {
	background-color: rgba(0,0,0,0.1);
	outline: none;
  } */
  .button.is-checked::after {
    background: #0071DC;
} 
  
  .button-group {
	margin: 20px 0 50px 0;
	text-align: center;
  }
  
  .button-group:after {
	content: '';
	display: block;
	clear: both;
  }
  
  article > img {
	vertical-align: middle;
  }
  
  .img-responsive {
	display: block;
	width: 100%;
	height: auto;
	border-radius: 10px;
  }
  
  .grid article {
	background-color: #FFFFFF;
	display: block;
	float: left;
	margin: 1%;
	width: 23%;
	border: 1px solid #C0C9D5;
    padding: 18px;
    border-radius: 6px;
  }
  .grid article:hover .gallery-after {
    transform: scale(1);
  }



